const Web3 = require("web3");

const Web3Quorum = require("../src");

const web3 = new Web3Quorum(
  new Web3("http://localhost:22000"),
  {
    privateUrl: "http://localhost:9081",
  },
  true
);

const accAddress = "ed9d02e382b34818e88b88a309c7fe71e65f419d";

const signAcct = web3.eth.accounts.decrypt(
  {
    address: accAddress,
    crypto: {
      cipher: "aes-128-ctr",
      ciphertext:
        "4e77046ba3f699e744acb4a89c36a3ea1158a1bd90a076d36675f4c883864377",
      cipherparams: { iv: "a8932af2a3c0225ee8e872bc0e462c11" },
      kdf: "scrypt",
      kdfparams: {
        dklen: 32,
        n: 262144,
        p: 1,
        r: 8,
        salt:
          "8ca49552b3e92f79c51f2cd3d38dfc723412c212e702bd337a3724e8937aff0f",
      },
      mac: "6d1354fef5aa0418389b1a5d1f5ee0050d7273292a1171c51fd02f9ecff55264",
    },
    id: "a65d1ac3-db7e-445d-a1cc-b6c5eeaa05e0",
    version: 3,
  },
  ""
);

const abi = [
  {
    constant: true,
    inputs: [],
    name: "storedData",
    outputs: [{ name: "", type: "uint256" }],
    payable: false,
    type: "function",
  },
  {
    constant: false,
    inputs: [{ name: "x", type: "uint256" }],
    name: "set",
    outputs: [],
    payable: false,
    type: "function",
  },
  {
    constant: true,
    inputs: [],
    name: "get",
    outputs: [{ name: "retVal", type: "uint256" }],
    payable: false,
    type: "function",
  },
  {
    inputs: [{ name: "initVal", type: "uint256" }],
    payable: false,
    type: "constructor",
  },
];

const bytecode =
  "";

const simpleContract = new web3.eth.Contract(abi);

const bytecodeWithInitParam = simpleContract
  .deploy({ data: bytecode, arguments: [42] })
  .encodeABI();

(async () => {
  try {
    const txCount = await web3.eth.getTransactionCount(`0x${accAddress}`);
    const tx = await web3.priv.generateAndSendRawTransaction({
      gasPrice: 0,
      gasLimit: 4300000,
      value: 0,
      data: bytecodeWithInitParam,
      from: signAcct,
      isPrivate: true,
      privateFrom: "BULeR8JyUWhiuuCMU/HLA0Q5pzkYT+cHII3ZKBey3Bo=",
      privateFor: [
        "QfeDAys9MPDs2XHExtc84jKGHxZg/aj52DTh0vtA3Xc=",
        "BULeR8JyUWhiuuCMU/HLA0Q5pzkYT+cHII3ZKBey3Bo=",
      ],
      nonce: txCount,
      privacyFlag: 2,
      mandatoryFor: ["QfeDAys9MPDs2XHExtc84jKGHxZg/aj52DTh0vtA3Xc="],
    });

    console.log("Contract address: ", tx.contractAddress);
    const simpleContract2 = new web3.eth.Contract(abi, tx.contractAddress);

    const val = await simpleContract2.methods.get().call();
    console.log(`Value: ${val}`);

    console.log("Privacy metadata for address: ", tx.contractAddress);
    const meta = await web3.eth.getContractPrivacyMetadata(tx.contractAddress);
    console.log("meta :>> ", meta);
    return tx;
  } catch (error) {
    console.log("error :>> ", error);
    return error;
  }
})();
